INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42070, 'Betamethasone', 'Trovafloxacin', 'Major', 'Concomitant administration of corticosteroids may potentiate the risk of tendinitis and tendon rupture associated with fluoroquinolone treatment. The mechanism is unknown. Tendinitis and tendon rupture have most frequently involved the Achilles tendon, although cases involving the rotator cuff (the shoulder), the hand, the biceps, and the thumb have also been reported. Some have required surgical repair or resulted in prolonged disability. Tendon rupture can occur during or up to several months after completion of fluoroquinolone therapy.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.g., age over 60 years; recipient of kidney, heart, and/or lung transplant). Patients should be advised to stop taking the fluoroquinolone, avoid exercise and use of the affected area, and promptly contact their physician if they experience pain, swelling, or inflammation of a tendon.', 'Synergism', 'Ciclesonide, Mupirocin, Mometasone furoate, Budesonide, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42126/', '[1] Khaliq Y, Zhanel GG "Fluoroquinolone-Associated Tendinopathy: A Critical Review of the Literature." Clin Infect Dis 36 (2003): 1404-1410[2] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[3] van der Linden PD, Sturkenboom MC, Herings RM, Leufkens HM, Rowlands S, Stricker BH "Increased risk of achilles tendon rupture with quinolone antibacterial use, especially in elderly patients taking oral corticosteroids." Arch Intern Med 163 (2003): 1801-7[4] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[5] "Product Information. Baxdela (delafloxacin)." Melinta Therapeutics, Lincolnshire, IL.[6] FDA. U.S. Food and Drug Administration "Information for Healthcare Professionals. Fluoroquinolone Antimicrobial Drugs. FDA Alert [7/8/2008]. Available from: URL: http://www.fda.gov/cder/drug/InfoSheets/HCP/fluoroquinolonesHCP.htm." ([7/8/2008]):[7] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42071, 'Betamethasone', 'Tuberculin purified protein derivative', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42127/', '[1] "Product Information. Spherulin (coccidioidin skin test)." ALK Laboratories Inc, Wallingford, CT.[2] "Product Information. Tuberculin PPD (Mantoux) (tuberculin purified protein derivative)." Connaught Laboratories Ltd, Toronto, ON.[3] "Product Information. Candin (candida albicans extract)." Nielsen Biosciences Inc, San Diego, CA.[4] "Product Information. MSTA Mumps Skin Test Antigen (mumps skin test antigen)." Aventis Pharmaceuticals, Swiftwater, PA.[5] "Product Information. Multitest CMI (skin test antigens, multiple)." Aventis Pharmaceuticals, Swiftwater, PA.[6] "Product Information. Histolyn-Cyl (histoplasmin)." ALK Laboratories Inc, Wallingford, CT.', 1767369485);
