INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42170, 'Ergometrine', 'Betaxolol (ophthalmic)', 'Moderate', 'Several case reports have suggested that beta-blockers may potentiate the vasoconstrictive action of ergot alkaloids. The exact mechanism of interaction is unknown, but may involve blockade of beta-2-mediated (i.e., sympathetic) vasodilatation. In addition, beta-1 blockade reduces cardiac output, which can diminish blood flow and exacerbate ergot-induced vasospasm.', NULL, 'Except for increased awareness of the interaction, no specific intervention appears to be necessary.', NULL, 'Except for increased awareness of the interaction, no specific intervention appears to be necessary. Patients should be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Latanoprost, Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Clonidine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, More', 'Misoprostol, Dinoprostone, Oxytocin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42228/', '[1] "Product Information. Cafergot (caffeine-ergotamine)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Venter CP, Joubert PH, Buys AC "Severe peripheral ischaemia during concomitant use of beta blockers and ergot alkaloids." Br Med J 289 (1984): 288-9[3] "Product Information. Migranal (dihydroergotamine nasal)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Blank NK, Rieder MJ "Paradoxica