INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42223, 'Ivabradine', 'Betaxolol (ophthalmic)', 'Moderate', 'Coadministration of ivabradine with other drugs that can slow the heart rate such as beta-blockers, calcium channel blockers, and digitalis may increase the risk of excessive bradycardia or other conduction disturbances. Non-cardiac drugs that may also cause bradycardia include atazanavir, fingolimod, lacosamide, lithium, mefloquine, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, 'Caution is advised when ivabradine is prescribed with other bradycardic drugs.', NULL, 'Caution is advised when ivabradine is prescribed with other bradycardic drugs. Heart rate should be closely monitored, and the dosage of ivabradine adjusted accordingly. Patients should be advised to report significant decreases in heart rate or symptoms such as dizziness, lightheadedness, fatigue, and hypotension.', 'Synergism', 'Latanoprost, Apraclonidine, Methazolamide, Clonidine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, Acetazolamide, Brinzolamide, Epinephrine, More', 'Alprostadil, Indomethacin, Ranolazine, Ibuprofen, Ubidecarenone, Adenosine, Regadenoson, Acetylsalicylic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42282/', '[1] "Product Information. Corlanor (ivabradine)." Amgen USA, Thousand Oaks, CA.[2] "Product Information. Corlanor (ivabradine)." Amgen USA, Thousand Oaks, CA.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
