INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42462, 'Isoprenaline', 'Betaxolol', 'Moderate', 'Some beta-blockers may antagonize the bronchodilatory, hypotensive, and tachycardic effects of isoproterenol. The mechanism is blockade of beta-adrenergic receptors, which leads to bronchoconstriction, vasodilation, and increased heart rate. Beta-blockers have been used successfully to treat catecholamine or isoproterenol-induced tachyarrhythmias.', NULL, 'This combination should generally be avoided.', NULL, 'This combination should generally be avoided. Patients who are receiving isoproterenol for cardiac conditions should be closely monitored for adequate therapeutic effect if a beta blocker is added. If no alternative exists, small doses of a B-1 selective beta-blocker (e.g., acebutolol, atenolol, betaxolol, bisoprolol, or metoprolol) may be preferable; however, respiratory status should be closely monitored in patients with obstructive pulmonary disease.', 'Antagonism', 'Latanoprost, Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Clonidine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, More', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Methoxamine, Mometasone furoate, Umeclidinium, Aclidinium, Mometasone, Fluticasone, Cromoglicic acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42524/', '[1] Ziegler MG, Chernow B, Woodson LC, Coyle J, Cruess D, Lake CR "The effect of propranolol on catecholamine clearance." Clin Pharmacol Ther 40 (1986): 116-9[2] Pickles H, Perucca E, Fish A, Richens A "Propranolol and sotalol as antagonists of isoproterenol-enhanced physiologic tremor." Clin Pharmacol Ther 30 (1981): 303-10[3