INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348918, 'Anakinra', 'Tocilizumab', 'Major', 'Coadministration of interleukin-6 (IL-6) inhibitors with other biologic disease-modifying antirheumatic drugs (DMARDs) may result in increased immunosuppression and a greater risk of infection. Treatment with IL-6 inhibitors has been associated with serious, potentially life-threatening and fatal infections, including tuberculosis, invasive fungal infections such as candidiasis, aspergillosis and pneumocystis, and other opportunistic infections.', 'DDInter', 1767317475, 'Concomitant use of IL-6 inhibitors with other biologic DMARDs should be avoided.', 'Others', 'Concomitant use of IL-6 inhibitors with other biologic DMARDs should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
