INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349161, 'Human C1-esterase inhibitor', 'Testosterone', 'Moderate', 'Thromboembolism may occur during treatment with C1 esterase inhibitors, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317475, 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators. Close monitoring for thromboembolic events is recommended during and after administration of C1 esterase inhibitors. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.', 'Synergism', 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349162, 'Calaspargase pegol', 'Testosterone', 'Moderate', 'Concomitant use of asparaginase with other hepatotoxic agents may potentiate the risk of liver injury. Hepatomegaly, acute severe hepatotoxicity, and fatal liver failure have been reported with asparaginase treatment in adults. Also, asparaginase may increase the toxicity of drugs bound to plasma proteins or metabolized by the liver.', 'DDInter', 1767317475, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.g., alcohol, androgens, antituberculosis agents, azole antifungal agents, ACE inhibitors, macrolide antibiotics, nonsteroidal anti-inflammatory agents, nucleoside reverse transcriptase inhibitors, sulfonamides, thiazolidinediones, and statins). Liver function tests should be monitored at regular intervals during asparaginase treatment with or without other hepatotoxic drugs. Patients should be advised to seek medical attention if they experience potential symptoms of hepatotoxicity such as right upper quadrant pain, increasing abdominal size, fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, dark urine, pale stools, and jaundice.', 'Synergism', 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349163, 'Calcipotriol (topical)', 'Testosterone', 'Moderate', 'Elevations in serum calcium levels have been observed in patients treated with topical vitamin D analogs. Concomitant use with systemic vitamin D products, calcium supplements, or other agents that can increase serum calcium concentrations such as thiazide diuretics, estrogens, anabolic steroids, and parathyroid hormone or parathyroid hormone analogs may increase the risk of clinically significant hypercalcemia.', 'DDInter', 1767317475, 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', 'Synergism', 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349164, 'Calcitriol (topical)', 'Testosterone', 'Moderate', 'Elevations in serum calcium levels have been observed in patients treated with topical vitamin D analogs. Concomitant use with systemic vitamin D products, calcium supplements, or other agents that can increase serum calcium concentrations such as thiazide diuretics, estrogens, anabolic steroids, and parathyroid hormone or parathyroid hormone analogs may increase the risk of clinically significant hypercalcemia.', 'DDInter', 1767317475, 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', 'Synergism', 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349165, 'Cannabidiol', 'Testosterone', 'Moderate', 'Coadministration of cannabidiol with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Cannabidiol causes dose-related elevations of liver transaminases, both alanine aminotransferase (ALT) and aspartate aminotransferase (AST).', 'DDInter', 1767317475, 'Caution is advised if cannabidiol is used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa. Serum transaminases and total bilirubin levels should be obtained prior to initiating cannabidiol, and patients with elevated baseline transaminase levels above 3 times the ULN accompanied by elevations in bilirubin above 2 times the ULN should be evaluated. Patients who develop clinical signs or symptoms suggestive of hepatic dysfunction (e.g., unexplained nausea, vomiting, right upper quadrant abdominal pain, fatigue, anorexia, jaundice, dark urine) should have serum transaminases and total bilirubin measured promptly, and cannabidiol treatment interrupted or discontinued as appropriate. Cannabidiol should be discontinued in patients with elevations of transaminase levels greater than 3 times the ULN and bilirubin levels greater than 2 times the ULN. Patients with sustained transaminase elevations of greater than 5 times the ULN should also have treatment discontinued. Patients with prolonged elevations of serum transaminases should be evaluated for other possible causes. Also consider dosage adjustment or discontinuation of any coadministered medication that is known to affect the liver.', 'Synergism', 'Caution is advised if cannabidiol is used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349166, 'Carfilzomib', 'Testosterone', 'Major', 'Venous thromboembolic events, including deep vein thrombosis (DVT) and pulmonary embolism (PE), have occurred during treatment with carfilzomib, and concurrent use of other agents also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317475, 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis. Thromboprophylaxis should be considered according to local treatment protocols. Close monitoring for thromboembolic events is recommended in patients with known risk factors (e.g., prior thrombosis, hyperlipidemia, hypertension, smoking) for thromboembolism. Patients should be advised to seek immediate medical attention if they develop symptoms of thromboembolism, such as shortness of breath, chest pain, hemoptysis, or arm or leg swelling or pain.', 'Synergism', 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349167, 'Cenobamate', 'Testosterone', 'Moderate', 'Coadministration with cenobamate may decrease the plasma concentrations and pharmacologic effects of CYP450 3A4 substrates. The proposed mechanism is cenobamate-mediated induction of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if cenobamate is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic index. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever cenobamate is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if cenobamate is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349168, 'Chlorpropamide', 'Testosterone', 'Moderate', 'The hypoglycemic effect of insulin secretagogues (e.g., sulfonylureas, meglitinides) may be potentiated by certain drugs, including ACE inhibitors, 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), nonsteroidal anti-inflammatory drugs (NSAIDs), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinine, quinidine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, fibrates, ginseng); stimulating insulin secretion (salicylates, NSAIDs, disopyramide, quinine, quinidine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Or, they may increase plasma concentration of insulin secretagogues by displacing them from plasma protein binding sites and/or inhibiting their metabolism (fibrates, NSAIDs, salicylates, sulfonamides).', 'DDInter', 1767317475, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment. The oral antidiabetic dosage(s) may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their doctor if it occurs. Patients should be observed for loss of glycemic control when these drugs are withdrawn.', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349169, 'Clofarabine', 'Testosterone', 'Moderate', 'The liver is a known target organ for clofarabine toxicity, and concomitant use of other potentially hepatotoxic agents may increase the risk of liver injury. Severe and fatal hepatotoxicity has occurred with the use of clofarabine alone.', 'DDInter', 1767317475, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Hepatic function should be monitored during clofarabine administration, and therapy discontinued if grade 3 to 4 liver enzyme or bilirubin elevations occur. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
