INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349174, 'Cyclosporine', 'Testosterone', 'Moderate', 'Coadministration with androgens or anabolic steroids may increase the blood concentrations of cyclosporine. The exact mechanism of interaction is unknown but may involve competitive inhibition of the CYP450 3A4 metabolism of cyclosporine.', 'DDInter', 1767317475, 'Caution is advised if cyclosporine is prescribed with androgens or anabolic steroids. Cyclosporine blood levels, renal function, and liver function tests should be monitored frequently and the dosage adjusted accordingly, particularly following initiation, discontinuation or change of androgen dosage in patients who are stabilized on their cyclosporine regimen. Patients should be advised to notify their physician if they experience possible signs of cyclosporine toxicity such as nausea, vomiting, diarrhea, abdominal pain, dizziness, fatigue, headache, tremors, and convulsions.', 'Metabolism', 'Caution is advised if cyclosporine is prescribed with androgens or anabolic steroids.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349175, 'Dabrafenib', 'Testosterone', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9. Dabrafenib has been found in vitro to be an inducer of these isoenzymes.', 'DDInter', 1767317475, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dabrafenib is added to or withdrawn from therapy. Significantly reduced plasma concentrations and loss of efficacy may occur with sensitive substrates of CYP450 3A4 or 2C9 such as hormonal contraceptives, immunosuppressants (cyclosporine, everolimus, sirolimus, tacrolimus), ivacaftor, and warfarin-type anticoagulants. Alternatives to these medications should be considered if possible.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349176, 'Darunavir', 'Testosterone', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by darunavir.', 'DDInter', 1767317475, 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darunavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
