INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349182, 'Dicoumarol', 'Testosterone', 'Major', 'Androgens and anabolic steroids may potentiate the hypoprothrombinemic response to oral anticoagulants and increase the risk of bleeding. The onset of interaction is generally observed within 2 to 3 days. The mechanism is unknown.', 'DDInter', 1767317475, 'During concomitant therapy, the INR and/or PT should be monitored closely and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of the androgenic agent in patients who are stabilized on their anticoagulation regimen. Significant anticoagulant dose reductions may be required. Some experts recommend avoiding this combination altogether. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'During concomitant therapy, the INR and/or PT should be monitored closely and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of the androgenic agent in patients who are stabilized on their anticoagulation regimen.', NULL, NULL, 'Major', 'pharmacodynamic');
