INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349220, 'Isavuconazonium', 'Testosterone', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or P-glycoprotein (P-gp). The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity and/or P-gp-mediated efflux of these drugs by isavuconazole.', 'DDInter', 1767317475, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever isavuconazonium sulfate is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349221, 'Itraconazole', 'Testosterone', 'Moderate', 'Coadministration with itraconazole may significantly increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by itraconazole.', 'DDInter', 1767317475, 'Caution is advised if itraconazole must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever itraconazole is added to or withdrawn from therapy. Some authorities recommend avoiding concomitant use of nevirapine from 2 weeks before and during treatment with itraconazole.', 'Metabolism', 'Caution is advised if itraconazole must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349222, 'Testosterone', 'Lapatinib', 'Moderate', 'Coadministration with lapatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these routes due to inhibition by lapatinib.', 'DDInter', 1767317475, 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lapatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349223, 'Testosterone', 'Larotrectinib', 'Moderate', 'Coadministration with larotrectinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever larotrectinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. Some authorities recommend avoiding concomitant use of larotrectinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349224, 'Testosterone', 'Lefamulin', 'Moderate', 'When administered orally, lefamulin may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Based on interaction with midazolam, a sensitive CYP450 3A4 substrate, lefamulin may be a moderate CYP450 3A4 inhibitor. When oral midazolam was administered concomitantly with and at 2 or 4 hours after administration of lefamulin tablets, mean midazolam peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 100% and 200%, respectively.', 'DDInter', 1767317475, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vardenafil, vinca alkaloids). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever oral lefamulin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349225, 'Testosterone', 'Leflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', 'DDInter', 1767317475, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), and vice versa. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349226, 'Testosterone', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or organic anion transporting polypeptide protein (OATP) 1B1 and 1B3. Letermovir has been shown to be a reversible inhibitor of CYP450 2C8 in vitro, although its effect on CYP450 2C8 substrates has not been evaluated clinically. Letermovir is also a time-dependent inhibitor and inducer of CYP450 3A4 in vitro.', 'DDInter', 1767317475, 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever letermovir is added to or withdrawn from therapy. Moreover, clinicians should be aware that the magnitude of CYP450 3A- and OATP1B1/3-mediated drug interactions with coadministered drugs may be different when letermovir is used with cyclosporine. The combined effect of the two drugs on CYP450 3A4 may be similar to that of a strong CYP450 3A4 inhibitor, hence clinicians should refer to the prescribing information for dosing recommendations of the CYP450 3A4 substrate with a strong CYP450 3A4 inhibitor. Similarly, letermovir and cyclosporine may demonstrate some additive effects on OATP1B1 inhibition, although cyclosporine by itself is already a strong OATP1B1/3 inhibitor.', 'Metabolism', 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349227, 'Testosterone', 'Levothyroxine', 'Moderate', 'Androgens may induce reversible clinical hyperthyroidism in patients receiving thyroid hormone replacement therapy. The proposed mechanism is androgen-induced decrease in T4 binding globulin resulting in decreased serum T4, increased T3 uptake resin and free T4, and decreased TSH levels.', 'DDInter', 1767317475, 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', 'Synergism', 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', NULL, NULL, 'Moderate', 'pharmacodynamic');
