INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349331, 'Crizotinib', 'Propofol', 'Moderate', 'Coadministration with crizotinib may theoretically increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2B6 (e.g., bupropion), P-glycoprotein (P-gp) (e.g., dabigatran), and/or the organic cation transporters 1 and 2 (OCT1, OCT2) (e.g., metformin). The proposed mechanism, based on in vitro data, is decreased clearance due to crizotinib-mediated inhibition of CYP450 2B6, P-gp, and/or OCT1 or OCT2.', 'DDInter', 1767317475, 'Although the clinical significance is unknown, caution is advised if crizotinib is used concomitantly with drugs that are substrates of CYP450 2B6, P-gp, and/or OCT1 or OCT2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever crizotinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Although the clinical significance is unknown, caution is advised if crizotinib is used concomitantly with drugs that are substrates of CYP450 2B6, P-gp, and/or OCT1 or OCT2, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1349332, 'Dabrafenib', 'Propofol', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9. Dabrafenib has been found in vitro to be an inducer of these isoenzymes.', 'DDInter', 1767317475, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dabrafenib is added to or withdrawn from therapy. Significantly reduced plasma concentrations and loss of efficacy may occur with sensitive substrates of CYP450 3A4 or 2C9 such as hormonal contraceptives, immunosuppressants (cyclosporine, everolimus, sirolimus, tacrolimus), ivacaftor, and warfarin-type anticoagulants. Alternatives to these medications should be considered if possible.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9.', NULL, NULL, 'Moderate', 'pharmacodynamic');
