INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42510, 'Betaxolol', 'Methoxyflurane', 'Moderate', 'The antihypertensive effects of methoxyflurane and beta blockers may be additive. The combination may cause profound hypotension. Older anesthetics such as methoxyflurane have more severe circulatory depressant effects in the presence of beta-blockade.', NULL, 'It is currently recommended that beta blocker therapy not be routinely withdrawn prior to surgery.', NULL, 'It is currently recommended that beta blocker therapy not be routinely withdrawn prior to surgery. However, in general, patients taking beta blockers should not be anaesthetized with methoxyflurane. Instead, an anesthetic with minimal negative inotropic activity may be considered. If methoxyflurane must be used, beta blocker therapy should be withdrawn gradually and generally completed 24 to 48 hours before anesthesia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42572/', '[1] "Product Information. Lopressor (metoprolol)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Hunter JM "Synergism between halothane and labetalol." Anaesthesia 34 (1979): 257-9[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Scott DB, Buckley FP, Littlewood DG, Macrae WR, Arthur GR, Drummond GB "Circulatory effects of labetalol during halothane anaesthesia." Anaesthesia 33 (1978): 145-56', 1767365204);
