INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42819, 'Betrixaban', 'Midostaurin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of betrixaban, which is a substrate of the efflux transporter.', NULL, 'Caution is advised when betrixaban is used with P-gp inhibitors.', NULL, 'Caution is advised when betrixaban is used with P-gp inhibitors. Closer monitoring of the pharmacologic effects of betrixaban may be appropriate whenever a P-gp inhibitor is added to or withdrawn from therapy. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women).', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42881/', '[1] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals, South San Francisco, CA.[2] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals, South San Francisco, CA.[3] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals, South San Francisco, CA.[4] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42820, 'Betrixaban', 'Mifepristone', 'Major', 'Concomitant use of anticoagulants may exacerbate mifepristone-induced vaginal bleeding in women. Prolonged, potentially heavy bleeding lasting approximately 9 to 30 days typically follows mifepristone use in termination of pregnancy. Because it is a progesterone receptor antagonist, mifepristone can also promote unopposed endometrial proliferation that may result in endometrium thickening, cystic dilatation of endometrial glands, and vaginal bleeding when used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome.', NULL, 'Concurrent anticoagulant therapy is considered a contraindication in the use of mifepristone for pregnancy termination.', NULL, 'Concurrent anticoagulant therapy is considered a contraindication in the use of mifepristone for pregnancy termination. When used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, mifepristone treatment should be administered cautiously in women who are receiving anticoagulant therapy. Patients who experience vaginal bleeding should be referred to a gynecologist for further evaluation.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42882/', '[1] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[2] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[3] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[4] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.', 1767365204);
