INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42825, 'Betrixaban', 'Nelfinavir', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may significantly increase the plasma concentrations of betrixaban, which is a substrate of the efflux transporter. In the Acute Medically Ill Prevention with Extended Duration Betrixaban (APEX) Study, use of betrixaban at a 50% dosage reduction in combination with P-gp inhibitors or in the presence of severe renal impairment was associated with increased relative risks of bleeding, including major bleeding, compared to treatment with enoxaparin.', NULL, 'When used with P-gp inhibitors, the recommended dosage of betrixaban is an initial single dose of 80 mg followed by 40 mg once daily.', NULL, 'When used with P-gp inhibitors, the recommended dosage of betrixaban is an initial single dose of 80 mg followed by 40 mg once daily. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42887/', '[1] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals, South San Francisco, CA.[2] Bailey DG, Dresser GK "Natural products and adverse drug interactions." Can Med Assoc J 170 (2004): 1531-2[3] Ho PC, Ghose K, Saville D, Wanwimolruk S "Effect of grapefruit juice on pharmacokinetics and pharmacodynamics of verapamil enantiomers in healthy volunteers." Eur J Clin Pharmacol 56 (2000): 693-8[4] Pillai U, Muzaffar J, Sandeep S, Yancey A "Grapefruit juice and verapamil: a toxic cocktail." South Med J 102 (2009): 308-9[