INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42883, 'Betrixaban', 'Sunitinib', 'Moderate', 'The concomitant use of anticoagulants may theoretically increase the risk of sunitinib-induced bleeding. Severe and life-threatening tumor hemorrhage, hemoptysis, and pulmonary hemorrhage, and nonserious epistaxis have occurred during treatment with sunitinib.', NULL, 'Monitoring for clinical and laboratory signs of bleeding is recommended during concomitant therapy, including INR/PT and complete blood counts.', NULL, 'Monitoring for clinical and laboratory signs of bleeding is recommended during concomitant therapy, including INR/PT and complete blood counts. Patients should be advised to promptly report any potential signs of bleeding to their doctor, including nosebleeds, pain, swelling, or headache.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42945/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[3] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42884, 'Betrixaban', 'Suprofen (ophthalmic)', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42946/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42885, 'Betrixaban', 'Suvorexant', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of betrixaban, which is a substrate of the efflux transporter.', NULL, 'Caution is advised when betrixaban is used with P-gp inhibitors.', NULL, 'Caution is advised when betrixaban is used with P-gp inhibitors. Closer monitoring of the pharmacologic effects of betrixaban may be appropriate whenever a P-gp inhibitor is added to or withdrawn from therapy. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women).', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42947/', '[1] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals, South San Francisco, CA.[2] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals, South San Francisco, CA.[3] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals, South San Francisco, CA.[4] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42886, 'Betrixaban', 'Tacrolimus', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may significantly increase the plasma concentrations of betrixaban, which is a substrate of the efflux transporter. In the Acute Medically Ill Prevention with Extended Duration Betrixaban (APEX) Study, use of betrixaban at a 50% dosage reduction in combination with P-gp inhibitors or in the presence of severe renal impairment was associated with increased relative risks of bleeding, including major bleeding, compared to treatment with enoxaparin.', NULL, 'When used with P-gp inhibitors, the recommended dosage of betrixaban is an initial single dose of 80 mg followed by 40 mg once daily.', NULL, 'When used with P-gp inhibitors, the recommended dosage of betrixaban is an initial single dose of 80 mg followed by 40 mg once daily. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42948/', '[1] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals, South San Francisco, CA.[2] Bailey DG, Dresser GK "Natural products and adverse drug interactions." Can Med Assoc J 170 (2004): 1531-2[3] Ho PC, Ghose K, Saville D, Wanwimolruk S "Effect of grapefruit juice on pharmacokinetics and pharmacodynamics of verapamil enantiomers in healthy volunteers." Eur J Clin Pharmacol 56 (2000): 693-8[4] Pillai U, Muzaffar J, Sandeep S, Yancey A "Grapefruit juice and verapamil: a toxic cocktail." South Med J 102 (2009): 308-9[