INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43107, 'Interferon beta-1a', 'Bexarotene', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Oprelvekin, Elapegademase, Histamine, Aldesleukin, Pegademase, Glatiramer', 'Pertuzumab, Olaparib, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Enasidenib, Belzutifan, Mitotane, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43169/', '[1] "Product Information. Plegridy (peginterferon beta-1a)." Biogen Idec Inc, Cambridge, MA.[2] "Product Information. Betaseron (interferon beta-1b)." Berlex, Richmond, CA.[3] "Product Information. Rebif (interferon beta-1a)." Serono Laboratories Inc, Norwell, MA.[4] "Product Information. Avonex (interferon beta-1a)." Biogen, Cambridge, MA.', 1767369485);
