INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43281, 'Bexarotene', 'St. John''s Wort', 'Moderate', 'Coadministration with St. John''s wort may alter the pharmacokinetics of antineoplastic agents that are substrates of the CYP450 3A4 isoenzyme (e.g., abiraterone, bexarotene, bortezomib, busulfan, cyclophosphamide, dasatinib, docetaxel, doxorubicin, etoposide, exemestane, ifosfamide, imatinib, irinotecan, letrozole, paclitaxel, sorafenib, sunitinib, tamoxifen, temsirolimus, teniposide, thiotepa, toremifene, tretinoin, vandetanib, vinca alkaloids). The mechanism is induction of CYP450 3A4 metabolism by constituents of St. John''s wort, which is expected to reduce the systemic levels and pharmacologic effects of many of these agents. In contrast, some agents such as cyclophosphamide and ifosfamide are prodrugs that are converted to active metabolites by CYP450 3A4, thus pharmacologic effects may be enhanced by St. John''s wort.', NULL, 'Until more information is available, the use of St.', NULL, 'Until more information is available, the use of St. John''s wort should probably be avoided before and during the entire course of chemotherapy. It is not known how much time should elapse between the discontinuation of St. John''s wort and initiation of chemotherapy. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Enasidenib, Belzutifan, Mitotane, Aflibercept, More', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, Milnacipran, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, Amoxapine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43343/', '[1] Sparreboom A, Cox MC, Acharya MR, Figg WD "Herbal remedies in the United States: potential adverse interactions with anticancer agents." J Clin Oncol 22 (2004): 2489-503[2] Roby CA, Anderson GD, Kantor E, Dryer DA, Burstein AH "St John''s Wort: Effect on CYP3A4 activity." Clin Pharmacol Ther 67 (2000): 451-7[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43282, 'Bexarotene', 'Stavudine', 'Major', 'Coadministration with other drugs that are known to increase triglyceride levels or cause pancreatic toxicity may potentiate the risk of pancreatitis associated with the use of bexarotene.', NULL, 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity.', NULL, 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity. Other risk factors such as a history of pancreatitis, uncontrolled hyperlipidemia, excessive alcohol consumption, uncontrolled diabetes mellitus, or biliary tract disease should also be considered when weighing benefits versus risks of bexarotene therapy. All patients treated with bexarotene should have fasting blood lipids measured before initiating therapy, weekly during therapy until the lipid response to bexarotene is established (usually within two to four weeks), and at 8-week intervals thereafter.', 'Synergism', 'Pertuzumab, Olaparib, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Sotorasib, Enasidenib, More', 'Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Famciclovir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43344/', '[1] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[4] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767369485);
