INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43346, 'Bexarotene', 'Zalcitabine', 'Major', 'Coadministration with other drugs that are known to increase triglyceride levels or cause pancreatic toxicity may potentiate the risk of pancreatitis associated with the use of bexarotene.', NULL, 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity.', NULL, 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity. Other risk factors such as a history of pancreatitis, uncontrolled hyperlipidemia, excessive alcohol consumption, uncontrolled diabetes mellitus, or biliary tract disease should also be considered when weighing benefits versus risks of bexarotene therapy. All patients treated with bexarotene should have fasting blood lipids measured before initiating therapy, weekly during therapy until the lipid response to bexarotene is established (usually within two to four weeks), and at 8-week intervals thereafter.', 'Synergism', 'Pertuzumab, Olaparib, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Sotorasib, Enasidenib, More', 'Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Famciclovir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43408/', '[1] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[2] Cerner Multum, Inc. "UK Summary of Product Ch