INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43365, 'Bosutinib', 'Bicalutamide', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'Anastrozole, Fulvestrant, Exemestane, Letrozole, Megestrol acetate, Ethinylestradiol, Diethylstilbestrol, Medroxyprogesterone acetate', 'Imatinib, Dabrafenib, Dacomitinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Regorafenib, Alpelisib, Erlotinib, Ponatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43427/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43366, 'Brentuximab vedotin', 'Bicalutamide', 'Moderate', 'Coadministration of brentuximab vedotin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The risk of hepatic injury should be considered when brentuximab vedotin is used with other agents that are potentially hepatotoxic (e.', NULL, 'The risk of hepatic injury should be considered when brentuximab vedotin is used with other agents that are potentially hepatotoxic (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; HIV reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations. Patients experiencing new, worsening, or recurrent hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.', 'Synergism', 'Darolutamide, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Relugolix, Degarelix, Aminoglutethimide, Exemestane, Diethylstilbestrol, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43428/', '[1] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc, Bothell, WA.', 1767369485);
