INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (87112, 'Dimethyl fumarate', 'Monomethyl fumarate', 'Major', 'Coadministration of monomethyl fumarate (MMF) or its prodrugs, dimethyl fumarate and diroximel fumarate, with one another may result in elevated plasma concentrations of MMF and increase the risk of adverse effects such as lymphopenia, opportunistic infections (e.g., progressive multifocal leukoencephalopathy (PML), herpes zoster, herpes simplex, West Nile virus, cytomegalovirus, Candida, Aspergillus, Nocardia, Listeria monocytogenes, Mycobacterium tuberculosis), hepatotoxicity, and flushing. Both dimethyl fumarate and diroximel fumarate are metabolized to MMF, the primary active entity in the treatment of multiple sclerosis.', NULL, 'Coadministration of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate with one another is considered contraindicated.', NULL, 'Coadministration of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate with one another is considered contraindicated. According to the prescribing information, treatment with monomethyl fumarate may be initiated the day following discontinuation of dimethyl fumarate or diroximel fumarate. Likewise, diroximel fumarate may be started the day after stopping dimethyl fumarate.', 'Synergism', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/87213/', '[1] "Product Information. Vumerity (diroximel fumarate)." Alkermes, Inc, Cambridge, MA.[2] "Product Information. Tecfidera (dimethyl fumarate)." Biogen Inc, Cambridge, MA.[3] "Product Information. Vumerity (diroximel fumarate)." Alkermes, Inc, Cambridge, MA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (87113, 'Dimethyl fumarate', 'Mumps virus strain B level jeryl lynn live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/87214/', '[1] Fauci AS