INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (87570, 'Diphenhydramine', 'Sotalol', 'Moderate', 'The use of higher than recommended dosages of diphenhydramine (e.g., through abuse or misuse) has been associated with serious and potentially fatal cardiac adverse events, including cardiac arrest, and arrhythmia related to prolongation of the QT interval. Coadministration with class Ia (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Australian authorities recommend avoiding concomitant use of diphenhydramine and class Ia or class III antiarrhythmic agents.', NULL, 'Australian authorities recommend avoiding concomitant use of diphenhydramine and class Ia or class III antiarrhythmic agents. Additionally, patients should be counseled to not exceed the recommended dosage and frequency or duration of use of diphenhydramine, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Acetylsalicylic acid, Ivabradine', 'Tripelennamine, Clemastine, Tetracaine, Pheniramine, Ketotifen, Levocetirizine, Chlorcyclizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/87671/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Poluzzi E, Raschi E, Godman B, et.al "Pro-arrhythmic potential of oral antihistamines (H1): Combining adverse event reports with drug utilization data across Europe." PLoS One 10 (2014): epub[3] Husain Z, Hussain K, Nair R, Steinman R "Diphenhydramine induced QT prolongation and torsade de pointes: An uncommon effect of a common drug." Cardiology 17 (2010): 509-11[4] Shah A, Yousuf T, Ziffra J, Zaidi A, Raghuvir R "Diphenhydramine and QT prolongation - A rare cardiac side effect of a drug used in common practice." J Cardiol Cases 12 (2015): 126-9[5] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[6] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[7] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[8] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71', 1767369485);
