INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (87955, 'Diroximel fumarate', 'Monomethyl fumarate', 'Major', 'Coadministration of monomethyl fumarate (MMF) or its prodrugs, dimethyl fumarate and diroximel fumarate, with one another may result in elevated plasma concentrations of MMF and increase the risk of adverse effects such as lymphopenia, opportunistic infections (e.g., progressive multifocal leukoencephalopathy (PML), herpes zoster, herpes simplex, West Nile virus, cytomegalovirus, Candida, Aspergillus, Nocardia, Listeria monocytogenes, Mycobacterium tuberculosis), hepatotoxicity, and flushing. Both dimethyl fumarate and diroximel fumarate are metabolized to MMF, the primary active entity in the treatment of multiple sclerosis.', NULL, 'Coadministration of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate with one another is considered contraindicated.', NULL, 'Coadministration of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate with one another is considered contraindicated. According to the prescribing information, treatment with monomethyl fumarate may be initiated the day following discontinuation of dimethyl fumarate or diroximel fumarate. Likewise, diroximel fumarate may be started the day after stopping dimethyl fumarate.', 'Synergism', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88056/', '[1] "Product Information. Vumerity (diroximel fumarate)." Alkermes, Inc, Cambridge, MA.[2] "Product Information. Tecfidera (dimethyl fumarate)." Biogen Inc, Cambridge, MA.[3] "Product Information. Vumerity (diroximel fumarate)." Alkermes, Inc, Cambridge, MA.', 1767369485);
