INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43646, 'Abametapir (topical)', 'Bictegravir', 'Moderate', 'Based on in vitro inhibition data, a single topical application of abametapir lotion may increase plasma concentrations of drugs that are substrates of CYP450 3A4, CYP450 2B6, and CYP450 1A2 isoenzymes and increase their systemic concentrations. The proposed mechanism is high and prolonged systemic exposure to the metabolite abametapir carboxyl, which has been shown to be an in vitro inhibitor of CYP450 3A4, CYP450 2B6, and CYP450 1A2.', NULL, 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion.', NULL, 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion. If abametapir is used, monitor for increased toxicity of the CYP450 3A4, CYP450 2B6, and/or CYP450 1A2 substrate.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43708/', '[1] "Product Information. Xeglyze (abametapir topical)." Dr. Reddy''s Laboratories Inc, Upper Saddle River, NJ.', 1767363021);
