INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43674, 'Carvedilol', 'Bictegravir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of bictegravir.', NULL, 'The use of bictegravir with P-gp and/or BCRP inhibitors should generally be avoided.', NULL, 'The use of bictegravir with P-gp and/or BCRP inhibitors should generally be avoided. If concomitant use is required, caution and close clinical and laboratory monitoring, including renal function, are recommended. Dosage adjustments may be required whenever a P-gp and/or BCRP inhibitor is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43736/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.[3] "Product Information. Viread (tenofovir)." Gilead Sciences, Foster City, CA.', 1767363021);
