INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43684, 'Daclatasvir', 'Bictegravir', 'Moderate', 'Coadministration with daclatasvir may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by daclatasvir.', NULL, 'Caution is advised when daclatasvir is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when daclatasvir is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever daclatasvir is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43746/', '[1] "Product Information. Daklinza (daclatasvir)." Bristol-Myers Squibb, Princeton, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43685, 'Dalfampridine', 'Bictegravir', 'Moderate', 'The concomitant use of dalfampridine with drugs that are inhibitors of organic cation transporter 2 (OCT2) may theoretically reduce its renal clearance. Dalfampridine is primarily eliminated through renal excretion, approximately 60% of which is via active secretion by OCT2. Elevated levels of dalfampridine may increase the risk of CNS excitation and seizures.', NULL, 'Caution is advised when dalfampridine is used with OCT2 inhibitors.', NULL, 'Caution is advised when dalfampridine is used with OCT2 inhibitors. The benefit of concomitant use should be weighed against the potential risk of seizures.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43747/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] "Product Information. Ampyra (dalfampridine)." Acorda Therapeutics, Hawthorne, NY.', 1767363021);
