INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43810, 'Emtricitabine', 'Zalcitabine', 'Major', 'Concomitant use of the cytidine analog nucleoside reverse transcriptase inhibitors (NRTI) lamivudine and emtricitabine may inhibit the intracellular phosphorylation of one another to their respective active derivative in vivo. This could result in diminished antiretroviral effects of these drugs. However, clinical experience on the coadministration of cytidine analogs is lacking. This interaction may also occur with zalcitabine. In addition, the therapeutic efficacy of these drugs in combination appears limited, since lamivudine and emtricitabine have similar resistance profiles via mutation of the same viral reverse transcriptase gene (M184V).', NULL, 'The use of the cytidine analog NRTIs lamivudine, emtricitabine, or zalcitabine in any combination in an antiretroviral treatment regimen that consists of two NRTIs is not recommended.', NULL, 'The use of the cytidine analog NRTIs lamivudine, emtricitabine, or zalcitabine in any combination in an antiretroviral treatment regimen that consists of two NRTIs is not recommended. Local antiretroviral treatment experts should be consulted for current practice.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43872/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Veal GJ, Hoggard PG, Barry MG, Khoo S, Back DJ "Interaction between lamivudine (3TC) and other nucleoside analogues for intracellular phosphorylation." AIDS 10 (1996): 546-8[4] "Product Information. Epivir (lamivudine)." Glaxo Wellcome, Research Triangle Park, NC.[5] Department of Health andHuman Services "Guidelines for the Use of Antiretroviral Agents inHIV-1-Infected Adults and Adolescents. Available from: URL: https://aidsinfo.nih.gov/contentfiles/lvguidelines/AdultAndAdolescentGL.pdf." ([2015, April 8]):[6] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.[7] "Product Information. Viread (tenofovir)." Gilead Sciences, Foster City, CA.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (43811, 'Amprenavir', 'Binimetinib', 'Moderate', 'Coadministration with inducers of CYP450 1A2, P-glycoprotein (P-gp), and/or uridine diphosphate glucuronosyltransferase (UGT) 1A1 or 2B7 may decrease the plasma levels and therapeutic efficacy of binimetinib. The proposed mechanism involves enhanced metabolic clearance of binimetinib through induction of CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7 inducers.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7 inducers. Pharmacologic response to binimetinib should be monitored more closely whenever a CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7 inducer is added to or withdrawn from therapy, and the binimetinib dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/43878/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] "Product Information. Mektovi (binimetinib)." Array BioPharma Inc., Boulder, CO.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 17