        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1349847, 'Benzphetamine', 'Sodium citrate', 'Moderate', 'Alkalinization of the urine can decrease the renal elimination of amphetamines. Amphetamine effects may be prolonged, and the risk of toxicity may be increased.', NULL, 'Urinary alkalinizers should generally not be used with amphetamines, particularly in an amphetamine overdose situation.', NULL, 'Urinary alkalinizers should generally not be used with amphetamines, particularly in an amphetamine overdose situation.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
