    ) VALUES (1349870, 'Duloxetine', 'Sodium citrate', 'Minor', 'Coadministration of enteric-coated duloxetine with substances that raise gastrointestinal pH may result in earlier release of duloxetine from the formulation. The enteric coating is intended to resist drug dissolution until reaching a segment of the gastrointestinal tract where the pH exceeds 5.5. However, coadministration with aluminum- and magnesium-containing antacids (51 mEq) or famotidine has been shown to have no significant effect on the rate or extent of duloxetine absorption following administration of a 40 mg oral dose.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
