INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (44044, 'Dextromethorphan', 'Linezolid', 'Major', 'Coadministration of dextromethorphan with linezolid may increase the risk of serotonin syndrome, a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Dextromethorphan is a weak serotonin reuptake inhibitor whose serotonergic activity may be enhanced by monoamine oxidase inhibitors (MAOIs). Serious and fatal reactions have been reported, primarily with the antidepressant MAOIs. The risk should be lower with linezolid, a relatively weak, reversible MAOI.', NULL, 'Due to the risk of serotonin syndrome, concomitant use of dextromethorphan with MAOIs is considered contraindicated by manufacturers of dextromethorphan-containing products.', NULL, 'Due to the risk of serotonin syndrome, concomitant use of dextromethorphan with MAOIs is considered contraindicated by manufacturers of dextromethorphan-containing products. If coadministration with linezolid is required, patients should be monitored for the development of serotonin syndrome. Symptoms may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/44111/', '[1] Rivers N, Horner B "Possible lethal reaction between nar