INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (44358, 'Biperiden', 'Primidone', 'Moderate', 'Central anticholinergic agents may have additive central nervous system (CNS) effects with cannabinoids, barbiturates, opiates, and alcohol. These agents individually can cause cognitive and psychomotor impairment, drowsiness, and dizziness, thus concomitant use may result in more potent effects. In addition, the potential for abuse may be increased when central anticholinergic agents are combined with these drugs.', NULL, 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated.', NULL, 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/44425/', '[1] "Product Information. Artane (trihexyphenidyl)." Lederle Laboratories, Wayne, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (44359, 'Biperiden', 'Prochlorperazine', 'Moderate', 'Centrally-acting anticholinergic agents may antagonize the therapeutic effects of neuroleptic agents. Although these drugs have been used together clinically, the possibility of increased risk of adverse effects such as central nervous system depression and tardive dyskinesia should also be considered. In addition, excessive anticholinergic effects may occur in combination use, which can result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome.', NULL, 'Caution is advised if anticholinergic agents are used with neuroleptic agents, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, 'Caution is advised if anticholinergic agents are used with neuroleptic agents, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Prophylactic administration of anticholinergic agents is sometimes given clinically during neuroleptic therapy for drug-induced parkinsonism or extrapyramidal symptoms but may not always be appropriate.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/44426/', '[1] Hvizdos AJ, Bennett JA, Wells BG, Rappaport KB, Mendel SA "Anticholinergic psychosis in a patient receiving usual doses of haloperidol." Clin Pharm 2 (1983): 174-8[2] Warnes H, Lehmann HE, Ba