INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (44500, 'Bisacodyl', 'Lactulose', 'Moderate', 'In the treatment of portal-systemic encephalopathy, the loose stools produced by concomitant use of other laxatives may falsely suggest that adequate lactulose dosage has been achieved.', NULL, 'The use of other laxatives should generally be avoided when lactulose is prescribed for portal-systemic encephalopathy, especially during the initial phase of therapy.', NULL, 'The use of other laxatives should generally be avoided when lactulose is prescribed for portal-systemic encephalopathy, especially during the initial phase of therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/44567/', '[1] "Product Information. Cephulac (lactulose)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] "Product Information. Generlac (lactulose)." Morton Grove Pharmaceuticals Inc, Morton Grove, IL.[3] "Product Information. Enulose (lactulose)." United Research Laboratories/Mutual Pharmaceutical Company, Philadelphia, PA.[4] "Product Information. Chronulac (lactulose)." Hoechst Marion-Roussel Inc, Kansas City, MO.[5] "Product Information. Metamucil (psyllium)." Procter and Gamble Pharmaceutic, Cincinnati, OH.', 1767365204);
