    ) VALUES (1350276, 'Fenofibrate', 'Cholestyramine', 'Moderate', 'INTERVAL: Theoretically, bile acid sequestrants may bind to and decrease the gastrointestinal absorption of fenofibrate/fenofibric acid.', NULL, 'Fenofibrate/fenofibric acid should be taken at least 1 hour before or 4 to 6 hours after the bile acid sequestrant.', NULL, 'Fenofibrate/fenofibric acid should be taken at least 1 hour before or 4 to 6 hours after the bile acid sequestrant.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
