    ) VALUES (1350292, 'Fenofibrate', 'Epirubicin', 'Moderate', 'The concomitant or sequential use of epirubicin with agents known to induce hepatotoxicity may potentiate the risk of liver injury. Epirubicin undergoes elimination through the hepatobiliary system.', NULL, 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', NULL, 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
