    ) VALUES (1350337, 'Fenofibrate', 'Montelukast', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 2C9 may increase the plasma concentrations of montelukast, which is metabolized by these isoenzymes in addition to CYP450 3A4. When montelukast was administered with the potent CYP450 2C8/2C9 inhibitor gemfibrozil, montelukast systemic exposure (AUC) increased by 4.4-fold.', NULL, 'According to the product labeling, no dosage adjustment of montelukast is required when used in combination with gemfibrozil.', NULL, 'According to the product labeling, no dosage adjustment of montelukast is required when used in combination with gemfibrozil. However, it may be advisable to monitor patients for potentially increased adverse effects during coadministration with gemfibrozil or other CYP450 2C8/2C9 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
