        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1350365, 'Fenofibrate', 'Sirolimus', 'Moderate', 'Higher dosages of lipid-lowering agents may be required during concomitant administration of sirolimus or temsirolimus. Dose-related serum lipid abnormalities, including but not limited to increased serum cholesterol and triglycerides, have been frequently reported in patients treated with sirolimus or temsirolimus.', NULL, 'Close clinical monitoring of serum cholesterol and triglycerides is recommended if sirolimus or temsirolimus is prescribed to patients receiving lipid-lowering agents.', NULL, 'Close clinical monitoring of serum cholesterol and triglycerides is recommended if sirolimus or temsirolimus is prescribed to patients receiving lipid-lowering agents. Modification of the antihyperlipidemic regimen or dosages may be necessary.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
