INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (89471, 'Docetaxel', 'Siponimod', 'Major', 'Coadministration of siponimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of unintended additive immunosuppressive effects.', NULL, 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', NULL, 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated. Caution is advised during coadministration and for 3 to 4 weeks after the last dose of siponimod. When switching from drugs with prolonged immune effects to siponimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89573/', '[1] "Product Information. Mayzent (siponimod)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
