INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (89547, 'Dofetilide', 'Bretylium', 'Major', 'Dofetilide should not be used with Class I or other Class III antiarrhythmic agents due to the potential for additive effects on myocardial refractoriness. Many of these agents, including dofetilide, can also cause prolongation of the QT interval, thus concomitant use may increase the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes.', NULL, 'Class I (e.', NULL, 'Class I (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, ibutilide, sotalol) antiarrhythmic agents should be withheld for at least 3 half-lives before administering dofetilide. In the case of amiodarone with its unpredictable pharmacokinetics, dofetilide should not be initiated until serum amiodarone levels are below 0.3 mcg/mL or amiodarone has been withdrawn for at least three months.', 'Synergism', 'Quinidine, Amiodarone, Dronedarone, Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine', 'Dofetilide', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89649/', '[1] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[2] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767369485);
