INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45089, 'Bisoprolol', 'Sodium oxybate', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Acetylsalicylic acid, Ivabradine', 'Halothane, Thiopental, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Propofol, Enflurane, Esketamine, Sevoflurane, Etomidate, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45159/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998): 595[7] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001): 435-40[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[10] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45090, 'Bisoprolol', 'Salicylic acid (sodium)', 'Minor', 'High doses of salicylates may blunt the antihypertensive effects of beta-blockers. The proposed mechanism is inhibition of prostaglandin synthesis. Low-dose aspirin does not appear to affect blood pressure. In addition, beta-blockers may exert an antiplatelet effect, which may be additive with the effects of some salicylates. Metoprolol may also increase aspirin absorption and/or plasma concentrations of salicylates; however, the clinical significance of this effect is unknown. Data have been conflicting. Until more information is available, patients who require concomitant therapy should be monitored for altered antihypertensive response whenever a salicylate is introduced or discontinued, or when its dosage is modified.', NULL, '-', NULL, '-', 'Antagonism', 'Acetylsalicylic acid, Ivabradine, Penbutolol, Carteolol, Hydrochlorothiazide, Indapamide', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45160/', '[1] Keber I, Jerse M, Keber D, Stegnar M "The influence of combined treatment with propranolol and acetylsalicylic acid on platelet aggregation in coronary heart disease." Br J Clin Pharmacol 7 (1979): 287-91[2] Zanchetti A, Hansson L, Leonetti G, et al. "Low-dose aspirin does not interfere with the blood pressure-lowering effects of antihypertensive therapy." J Hypertens 20 (2002): 1015-1022[3] Sziegoleit W, Rausch J, Polak G, et al "Influence of acetylsalicylic acid on acute circulatory effects of the beta-blocking agents pindolol and propranolol in humans." Int J Clin Pharmacol Ther Toxicol 20 (1982): 423-30[4] Hartmann D, Stief G, Lingenfelder M, Guzelhan C, Horsch AK "Study on the possible interaction between tenoxicam and atenolol in hypertensive patients." Arzneimittelforschung 45-1 (1995): 494-8[5] Spahn H, Langguth P, Kirch W, et al "Pharmacokinetics of salicylates administered with metoprolol." Arzneimittelforschung 36 (1986): 1697-9[6] Sziegoleit W, Rausch J , Polak G, Gyorgy M, Dekov E, Bekes M "Influence of acetylsalicylic acid on acute circulatory effects of the beta-blocking agents pindolol and propranolol." Int J Clin Pharmacol Ther Toxicol 20 (1982): 423-30', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45091, 'Bisoprolol', 'Spironolactone', 'Moderate', 'Although they are often combined in clinical practice, diuretics and beta-blockers may increase the risk of hyperglycemia and hypertriglyceridemia in some patients, especially in patients with diabetes or latent diabetes. In addition, the risk of QT interval prolongation and arrhythmias (e.g. torsades de pointes) due to sotalol may be increased by potassium-depleting diuretics.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration. Patients should be advised to seek medical assistance if they experience dizziness, weakness, fainting, fast or irregular heartbeats, or loss of blood glucose control.', 'Synergism', 'Amiloride', 'Ivabradine, Esmolol, Carteolol, Penbutolol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45161/', '[1] Dean S, Kendall MJ, Potter S, Thompson MH, Jackson DA "Nadolol in combination with indapamide and xipamide in resistant hypertensives." Eur J Clin Pharmacol 28 (1985): 29-33[2] Dornhorst A, Powell SH, Pensky J "Aggravation by propranolol of hyperglycaemic effect of hydrochlorothiazide in type II diabetics without alteration of insulin secretion." Lancet 1 (1985): 123-6[3] "Product Information. Lozol (indapamide)." Rhone-Poulenc Rorer, Collegeville, PA.[4] Marcy TR, Ripley TL "Aldosterone antagonists in the treatment of heart failure." Am J Health Syst Pharm 63 (2006): 49-58[5] Roux A, Le Liboux A, Delhotal B, Gaillot J, Flouvat B "Pharmacokinetics in man of acebutolol and hydrochlorothiazide as single agents and in combination." Eur J Clin Pharmacol 24 (1983): 801-6', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45092, 'Bisoprolol', 'Succinylcholine', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', 'Botulinum toxin type A', 'Acetylsalicylic acid, Enalapril, Moexipril, Ramipril, Lisinopril, Perindopril, Benazepril, Quinapril, Trandolapril, Captopril, Fosinopril', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45162/', '[1] Loan PB, Connolly FM, Mirakhur RK, Kumar N, Farling P "Neuromuscular effects of rocuronium in patients receiving beta-adrenoreceptor blocking, calcium entry blocking and anticonvulsant drugs." Br J Anaesth 78 (1997): 90-1[2] Harrah MD, Way WL, Katzung BG "The interaction of d-tubocurarine with antiarrhythmic drugs." Anesthesiology 33 (1970): 406-10[3] Rozen MS, Whan FM "Prolonged curarization associated with propranolol." Med J Aust 1 (1972): 467-8[4] Murthy VS, Patel KD, Elangovan RG, Hwang TF, Solochek SM, Steck JD, Laddu AR "Cardiovascular and neuromuscular effects of esmolol during induction of anesthesia." J Clin Pharmacol 26 (1986): 351-7[5] Glynne GL "Drug interaction?" Anaesthesia 39 (1984): 293[6] Herishanu Y, Rosenberg P "Beta-blockers and myasthenia gravis." Ann Intern Med 83 (1975): 834-5[7] Yate B, Mostafa SM "Drug interaction?" Anaesthesia 39 (1984): 728-9[8] Chapple DJ, Clark JS, Hughes R "Interaction between atracurium and drugs used in anaesthesia." Br J Anaesth 55 Suppl 1 (1983): s17-22', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45093, 'Bisoprolol', 'Sucralfate', 'Minor', 'Concurrent administration with aluminum and magnesium antacids has been shown to decrease the oral bioavailability of certain beta-blockers, although data are conflicting. The exact mechanism of interaction is unknown but may involve cation binding of beta-blockers or a reduction in the dissolution rate due to increased gastric pH. Based on available data, the clinical significance of this potential interaction is difficult to determine. As a precaution, patients may want to consider separating the administration times of beta-blockers and antacids or other aluminum- or magnesium-containing products by at least 2 hours.', NULL, '-', NULL, '-', 'Absorption', 'Tinidazole, Tetracycline, Dexlansoprazole, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Esomeprazole, Vonoprazan, Levofloxacin', 'Ivabradine, Esmolol, Moexipril, Perindopril, Benazepril, Trandolapril, Fosinopril, Indapamide', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45163/', '[1] D''Arcy PF, McElnay JC "Drug-antacid interactions: assessment of clinical importance." Drug Intell Clin Pharm 21 (1987): 607-17[2] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol." Biopharm Drug Dispos 2 (1981): 79-87[3] Hong CY, Hu SC, Lin SJ, Chiang BN "Lack of influence of aluminum hydroxide on the bioavailability and beta-adrenoceptor blocking activity of propranolol." Int J Clin Pharmacol Ther Toxicol 23 (1985): 244-6[4] Kirch W, Schafer-Korting M, Axthelm T, Kohler H, Mutschler E "Interaction of atenolol with furos