INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45094, 'Bisoprolol', 'Sufentanil', 'Moderate', 'In patients receiving chronic beta-blocker therapy, the use of sufentanil may lead to a higher degree of bradycardia and hypotension. One study has shown that induction and maintenance of anesthesia requires lower doses of sufentanil in patients receiving beta-blockers.', NULL, 'Patients should be carefully observed for clinically significant alterations in hemodynamics.', NULL, 'Patients should be carefully observed for clinically significant alterations in hemodynamics.', 'Synergism', 'Acetylsalicylic acid, Ivabradine, Indapamide', 'Halothane, Thiopental, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Propofol, Enflurane, Esketamine, Sevoflurane, Etomidate, Ketamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45164/', '[1] Thomson IR, MacAdams CL, Hudson RJ, Rosenbloom M "Drug interactions with sufentanil: hemodynamic effects of premedication and muscle relaxants." Anesthesiology 76 (1992): 922-9[2] "Product Information. Sufenta (sufentanil)." Janssen Pharmaceutica, Titusville, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):', 1767369485);
