INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45221, 'Bivalirudin', 'Pentosan polysulfate', 'Moderate', 'Theoretically, pentosan polysulfate sodium may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', NULL, 'Caution is advised if pentosan polysulfate sodium is used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if pentosan polysulfate sodium is used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Protein C, Acenocoumarol, Selexipag, Desirudin', 'Darifenacin, Vardenafil, Tadalafil, Ammonium chloride, Apomorphine, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Calcium chloride, Trospium, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45291/', '[1] "Product Information. Elmiron (pentosan polysulfate sodium)." Baker Norton Pharmaceuticals, Miami, FL.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45222, 'Bivalirudin', 'Pentoxifylline', 'Moderate', 'The use of pentoxifylline has been associated with bleeding and/or prolongation in prothrombin time. Theoretically, the risk of bleeding may be increased by concomitant treatment with anticoagulants or other agents that affect hemostasis such as nonsteroidal anti-inflammatory drugs (NSAIDs), platelet inhibitors, thrombin inhibitors, thrombolytic agents, and dextran. There have been postmarketing reports of increased anticoagulant activity in patients receiving vitamin K antagonists in combination with pentoxifylline.', NULL, 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis.', NULL, 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis. Patients on vitamin K antagonists such as warfarin should have more frequent monitoring of INR following initiation or dosage change of pentoxifylline, while patients receiving other agents that affect hemostasis should have periodic examinations for bleeding including hematocrit and/or hemoglobin.', 'Synergism', 'Protein C, Acenocoumarol, Selexipag', 'Cyclandelate, Tolazoline, Phenoxybenzamine, Phentolamine, Isoxsuprine, Niacin, Theophylline, Omalizumab, Tezepelumab, Mercaptopurine, Aminophylline, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45292/', '[1] "Product Information. Trental (pentoxifylline)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45223, 'Bivalirudin', 'Phenylbutazone', 'Moderate', 'The safety and efficacy of bivalirudin have not been established when used in conjunction with platelet inhibitors other than aspirin. Theoretically, the concomitant administration of bivalirudin (plus aspirin) and platelet inhibitors may increase the risk of bleeding complications due to additive or synergistic effects on the clotting cascade. However, limited data from drug interaction studies do not suggest pharmacodynamic interactions with the platelet inhibitors, ticlopidine and abciximab.', NULL, 'Until further data are available, the coadministration of bivalirudin and antiplatelet agents, and possibly other agents with antiplatelet activity such as nonsteroidal anti-inflammatory drugs (NSAIDs), should probably be undertaken with caution.', NULL, 'Until further data are available, the coadministration of bivalirudin and antiplatelet agents, and possibly other agents with antiplatelet activity such as nonsteroidal anti-inflammatory drugs (NSAIDs), should probably be undertaken with caution. Close clinical and laboratory observation for bleeding complications is recommended.', 'Synergism', 'Clopidogrel, Dicoumarol, Protein C, Acenocoumarol, Selexipag, Anistreplase', 'Acetylsalicylic acid, Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Tolazoline, Rabeprazole, Dimethyl sulfoxide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45293/', '[1] "Product Information. Angiomax (bivalirudin)" The Medicines Compny, Cambridge, MA.', 1767369485);
