INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45239, 'Bivalirudin', 'Salicylic acid (sodium)', 'Moderate', 'Theoretically, salicylates may potentiate the effects of anticoagulants and increase the risk of bleeding. Salicylates interfere with the action of vitamin K and induce a dose-dependent alteration in hepatic synthesis of coagulation factors VII, IX and X, occasionally increasing the prothrombin time. While these effects are generally slight for most salicylates (except aspirin) at recommended dosages, they may be of clinical significance when combined with the inhibitory effects of anticoagulants on the clotting cascade. Moreover, salicylates are known to cause dose-related gastrointestinal bleeding, which may be complicated by anticoagulant therapy.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. The same precaution should be observed with the use of salicylate-related agents such as salicylamide because of their structural and pharmacological similarities.', 'Synergism', 'Cilostazol, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Streptokinase, Tinzaparin, Selexipag, Vorapaxar, Dicoumarol, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45309/', '[1] Wells PS, Holbrook AM, Crowther NR, Hirsh J "Interactions of warfarin with drugs and food." Ann Intern Med 121 (1994): 676-83[2] Fausa O "Salicylate-induced hypoprothrombinemia: a report of four cases." Acta Med Scand 188 (1970): 403-8[3] Koch-Weser J, Sellers EM "Drug interactions with coumarin anticoagulants (first of two parts)." N Engl J Med 285 (1971): 487-98[4] Koch-Weser J, Sellers EM "Drug interactions with coumarin anticoagulants (second of two parts)." N Engl J Med 285 (1971): 547-58[5] American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association; (1994):[6] Barrow MV, Quick DT, Cunningham RW "Salicylate hypoprothrombinemia in rheumatoid arthritis with liver disease. Report of two cases." Arch Intern Med 120 (1967): 620-4[7] Weiss HJ, Aledort LM, Kochwa S "The effect of salicylates on the hemostatic properties of platelets in man." J Clin Invest 47 (1968): 2169-80[8] Penning-van Beest F, Erkens J, Petersen KU, Koelz HR, Herings R "Main comedications associated with major bleeding during anticoagulant therapy with coumarins." Eur J Clin Pharmacol 61 (2005): 439-44', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45240, 'Bivalirudin', 'Streptokinase', 'Major', 'The concomitant administration of thrombin inhibitors and thrombolytic agents may increase the risk of bleeding complications. Intracranial bleeding has been reported in patients with acute myocardial infarction treated with argatroban or lepirudin and thrombolytic therapy (recombinant tPA or streptokinase). The mechanism may be attributable to complementary or synergistic effects of these agents on thrombin-dependent functions and activated partial thromboplastin time (aPPT).', NULL, 'Coadministration should be undertaken with caution and only after thorough assessment of risks and benefits.', NULL, 'Coadministration should be undertaken with caution and only after thorough assessment of risks and benefits. Close clinical and laboratory observation for bleeding complications is recommended.', 'Synergism', 'Alteplase, Streptokinase, Antithrombin III human, Tenecteplase, Acenocoumarol, Selexipag, Urokinase, Anistreplase, Reteplase', 'Antithrombin III human, Bivalirudin, Acenocoumarol, Selexipag, Acetylsalicylic acid, Cangrelor, Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Human C1-esterase inhibitor, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45310/', '[1] "Product Information. Refludan (lepirudin)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45241, 'Bivalirudin', 'Sugammadex', 'Moderate', 'Sugammadex may potentiate the effects of anticoagulants. The mechanism of interaction has not been described.', NULL, 'Hemostasis and coagulation parameters should be closely monitored when sugammadex is administered to patients receiving therapeutic anticoagulation for a preexisting or comorbid condition; patients who have received thromboprophylaxis drugs other than heparin and LMWH; or patients who have received thromboprophylaxis with heparin or LMWH and subsequently administered 16 mg/kg of sugammadex.', NULL, 'Hemostasis and coagulation parameters should be closely monitored when sugammadex is administered to patients receiving therapeutic anticoagulation for a preexisting or comorbid condition; patients who have received thromboprophylaxis drugs other than heparin and LMWH; or patients who have received thromboprophylaxis with heparin or LMWH and subsequently administered 16 mg/kg of sugammadex.', 'Others', 'Ticagrelor, Clopidogrel, Cilostazol, Iloprost, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Prasugrel, Streptokinase, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45311/', '[1] "Product Information. Bridion (sugammadex)." Merck & Company Inc, Whitehouse Station, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45242, 'Bivalirudin', 'Sulfinpyrazone', 'Moderate', 'The safety and efficacy of bivalirudin have not been established when used in conjunction with platelet inhibitors other than aspirin. Theoretically, the concomitant administration of bivalirudin (plus aspirin) and platelet inhibitors may increase the risk of bleeding complications due to additive or synergistic effects on the clotting cascade. However, limited data from drug interaction studies do not suggest pharmacodynamic interactions with the platelet inhibitors, ticlopidine and abciximab.', NULL, 'Until further data are available, the coadministration of bivalirudin and antiplatelet agents, and possibly other agents with antiplatelet activity such as nonsteroidal anti-inflammatory drugs (NSAIDs), should probably be undertaken with caution.', NULL, 'Until further data are available, the coadministration of bivalirudin and antiplatelet agents, and possibly other agents with antiplatelet activity such as nonsteroidal anti-inflammatory drugs (NSAIDs), should probably be undertaken with caution. Close clinical and laboratory observation for bleeding complications is recommended.', 'Synergism', 'Dipyridamole, Abciximab, Tirofiban, Caplacizumab, Clopidogrel, Cilostazol, Prasugrel, Eptifibatide, Antithrombin III human, Protein C, Acenocoumarol, More', 'Lesinurad, Febuxostat, Pegloticase, Colchicine, Rasburicase, Probenecid, Allopurinol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45312/', '[1] "Product Information. Angiomax (bivalirudin)" The Medicines Compny, Cambridge, MA.', 1767369485);
