INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45447, 'Filgrastim', 'Blinatumomab', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, hematopoietic growth factors should not be used within 24 hours before or 24 hours after administration of antineoplastic agents.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, hematopoietic growth factors should not be used within 24 hours before or 24 hours after administration of antineoplastic agents.', 'Antagonism', 'Interferon alfa-n1, Oprelvekin, Elapegademase, Peginterferon alfa-2a, Interferon alfacon-1, Histamine, Aldesleukin, Pegademase, Interferon alfa-2b, Peginterferon alfa-2b, Glatiramer', 'Pertuzumab, Cetuximab, Olaratumab, Avelumab, Elotuzumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Dostarlimab, Amivantamab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45517/', '[1] "Product Information. Leukine (sargramostim)." Immunex Corporation, Seattle, WA.[2] "Product Information. Granix (tbo-filgrastim)." Teva Pharmaceuticals USA, North Wales, PA.[3] "Product Information. Stemgen (ancestim)" Amgen, Thousand Oaks, CA.[4] "Product Information. Neupogen (filgrastim)." Amgen, Thousand Oaks, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45448, 'Fingolimod', 'Blinatumomab', 'Major', 'Coadministration of fingolimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of infections. Fingolimod causes reversible sequestration of lymphocytes in lymphoid tissues.', NULL, 'The safety and efficacy of fingolimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', NULL, 'The safety and efficacy of fingolimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated. Caution is advised during coadministration. A complete blood count is recommended prior to starting fingolimod if a recent one (i.e., within last 6 months) is not available.', 'Synergism', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Thalidomide, Belimumab, More', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45518/', '[1] "Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45449, 'Fluphenazine', 'Blinatumomab', 'Moderate', 'The risk of seizures may be increased during coadministration of fluphenazine with any substance that can reduce the seizure threshold, such as aminophylline, antidepressants, antipsychotics, opioids, carbapenems, etc. These agents are often individually epileptogenic and may have additive effects when combined. Many of these agents also exhibit CNS- and/or respiratory-depressant effects, which may be enhanced during their concomitant use with fluphenazine.', NULL, 'Caution is advised if fluphenazine is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.', NULL, 'Caution is advised if fluphenazine is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.g., head trauma, brain tumor, metabolic disorders, alcohol and drug withdrawal, CNS infections). Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'Haloperidol, Thiothixene, Risperidone, Amisulpride, Quetiapine, Prochlorperazine, Promazine, Thioridazine, Lumateperone, Pimozide, Trifluoperazine, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45519/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45450, 'Ginkgo biloba', 'Blinatumomab', 'Moderate', 'Certain preparations of ginkgo biloba have been reported to induce seizures. There may be a theoretical risk of increased seizure potential when used with other agents that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline. Ginkgo products may contain varying amounts of 4''-O-methylpyridoxine (ginkgotoxin), a known neurotoxin found primarily in ginkgo biloba seeds but also detected in lesser amounts in the leaves. In vivo, 4''-O-methylpyridoxine competes with vitamin B6, which causes an indirect inhibition of glutamate decarboxylase and subsequent decrease in the formation of gamma-aminobutyric acid (GABA) in the brain. There have been published case reports of generalized convulsions and vomiting within several hours after ingestion of large amounts of ginkgo nuts/seeds, including in young children and healthy individuals with no known personal or family history of epilepsy.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Because of inconsistencies in formulation and potency of commercial herbal preparations, there is no way to verify without laboratory testing if and in what quantity 4''-O-methylpyridoxine may be present in a given ginkgo preparation. Patients treated with agents that can lower the seizure threshold should preferably avoid the use of products containing ginkgo biloba.', 'Synergism', 'Rivastigmine, Galantamine, Donepezil, Tacrine, Memantine, Aducanumab', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45520/', '[1] Kajiyama Y, Fujii K, Takeuchi H, Manabe Y "Ginkgo seed poisoning." Pediatrics 109 (2002): 325-7[2] Granger AS "Ginkgo biloba precipitating epileptic seizures." Age Ageing 30 (2001): 523-5[3] Miller LG "Herbal medicinals: selected clinical considerations focusing on known or potential drug-herb interactions." Arch Intern Med 158 (1998): 2200-11[4] Gregory PJ "Seizure associated with Ginkgo biloba?." Ann Intern Med 134 (2001): 344[5] Harms SL, Garrard J, Schwinghammer P, Eberly LE, Chang Y, Leppik IE "Ginkgo biloba use in nursing home elderly with epilepsy or seizure disorder." Epilepsia 47 (2006): 323-9[6] Spinella M "Herbal medicines and epilepsy: the potential for benefit and adverse effects." Epilepsy Behav 2 (2001): 524-32[7] Miwa H, Iijima M, Tanaka S, Mizuno Y "Generalized convulsions after consuming a large amount of Gingko nuts." Epilepsia 42 (2001): 280-1[8] Kupiec T, Raj V "Fatal seizures due to potential herb-drug interactions with Ginkgo biloba." J Anal Toxicol 29 (2005): 755-8', 1767369485);
