INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45538, 'Calcium Phosphate', 'Burosumab', 'Major', 'Concomitant use of burosumab with oral phosphate and/or active vitamin D analogs may increase the risk of hyperphosphatemia and hypervitaminosis D due to additive pharmacologic effects. Increases in serum phosphorus to above the upper limit of normal may be associated with an increased risk of nephrocalcinosis.', NULL, 'Oral phosphate and active vitamin D analogs should be discontinued one week prior to initiation of burosumab treatment.', NULL, 'Oral phosphate and active vitamin D analogs should be discontinued one week prior to initiation of burosumab treatment. Fasting serum phosphorus concentration should be below the reference range for age prior to initiation of treatment.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45608/', '[1] "Product Information. Crysvita (burosumab)." Ultragenyx Pharmaceutical, Novato, CA.', 1767363021);
