INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45541, 'Calcium Phosphate', 'Calcipotriol (topical)', 'Moderate', 'Elevations in serum calcium levels have been observed in patients treated with topical vitamin D analogs. Concomitant use with systemic vitamin D products, calcium supplements, or other agents that can increase serum calcium concentrations such as thiazide diuretics, estrogens, anabolic steroids, and parathyroid hormone or parathyroid hormone analogs may increase the risk of clinically significant hypercalcemia.', NULL, 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', NULL, 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45611/', '[1] "Product Information. Dovonex (calcipotriene)." Westwood Squibb Pharmaceutical Corporation, Eatontown, NJ.[2] "Product Information. Vectical (calcitriol topical)." Galderma Laboratories Inc, Cranbury, NJ.', 1767363021);
