INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45598, 'Ginger', 'Lurbinectedin', 'Moderate', 'Coadministration with a CYP450 3A4 inhibitor may increase lurbinectedin systemic exposure, which may increase the incidence and severity of adverse reactions to lurbinectedin, such as myelosuppression and hepatotoxicity.', NULL, 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with lurbinectedin.', NULL, 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with lurbinectedin. Patients should be advised to contact their physician if they experience symptoms suggestive of myelosuppression or hepatotoxicity, such as fever or any other signs of infection, unusual bruising or bleeding; or nausea or vomiting, or pain on the right side of the abdomen.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45668/', '[1] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[3] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45599, 'Ginger', 'Lusutrombopag', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of lusutrombopag, which is a substrate of both efflux transporter proteins.', NULL, 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.', NULL, 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors. Pharmacologic response to lusutrombopag should be monitored more closely following initiation, discontinuation, or change of dosage of the concomitant P-gp or BCRP inhibitor.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45669/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):', 1767363021);
