INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45603, 'Folic acid', 'Methotrexate', 'Moderate', 'Coadministration of methotrexate with vitamin preparations containing folic acid, folinic acid (leucovorin) or their derivatives may diminish the therapeutic effects of methotrexate. The proposed mechanism involves pharmacodynamic antagonism and/or competitive inhibition at the receptor site.', NULL, 'Although folic acid or folinic acid are frequently used along with methotrexate to reduce adverse effects or as an antidote to high dose methotrexate therapy, patients receiving methotrexate should be advised to avoid the inadvertent use of vitamin preparations containing folic acid or its derivatives.', NULL, 'Although folic acid or folinic acid are frequently used along with methotrexate to reduce adverse effects or as an antidote to high dose methotrexate therapy, patients receiving methotrexate should be advised to avoid the inadvertent use of vitamin preparations containing folic acid or its derivatives.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45673/', '[1] "Product Information. Methotrexate Sodium (methotrexate)." Teva Pharmaceuticals (formerly IVAX), Miami, FL.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Nesher G, Mates M, Zevin S "Effect of caffeine consumption on efficacy of methotrexate in rheumatoid arthritis." Arthritis Rheum 48 (2003): 571-572', 1767363021);
