INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45641, 'Calcium Phosphate', 'Sarecycline', 'Moderate', 'INTERVAL: Administration of a tetracycline with aluminum, calcium, or magnesium salts significantly decreases tetracycline serum concentrations. The proposed mechanism is chelation of tetracycline by the cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract. The interaction has also been reported with parenteral doxycycline and oral antacids.', NULL, 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.', NULL, 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45711/', '[1] Nguyen VX, Nix DE, Gillikin S, Schentag JJ "Effect of oral antacid administration on the pharmacokinetics of intravenous doxycycline." Antimicrob Agents Chemother 33 (1989): 434-6[2] Gotz VP, Ryerson GG "Evaluation of tetracycline on theophylline disposition in patients with chronic obstructive airways disease." Drug Intell Clin Pharm 20 (1986): 694-6[3] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part I)." Clin Pharmacokinet 20 (1991): 66-80[4] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[5] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[6] McCormack JP, Reid SE, Lawson LM "Theophylline toxicity induced by tetracycline." Clin Pharm 9 (1990): 546-9[7] Neuvonen PJ "Interactions with the absorption of