INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45642, 'Secobarbital', 'Pyridoxine', 'Minor', 'One small study has suggested that pyridoxine may decrease serum levels of phenobarbital. The mechanism is unknown. Phenobarbital is the only barbiturate specifically implicated in this interaction. However, other barbiturates may behave in a similar fashion. Close observation for altered barbiturate effects is indicated if these drugs must be used together.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45712/', '[1] Hansson O, Sillanpaa M "Pyridoxine and serum concentration of phenytoin and phenobarbitone." Lancet 1 (1976): 256', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45643, 'Ginger', 'Selpercatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when selpercatinib is used concomitantly with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when selpercatinib is used concomitantly with CYP450 3A4 inhibitors. Patients should be closely monitored for development of serious adverse effects such as QT interval prolongation, liver transaminase and bilirubin elevations, hypertension, hemorrhage, edema and hypersensitivity reactions (e.g., fever, rash, arthralgias/myalgias with concurrent decreased platelets or transaminitis), and the dosing of selpercatinib adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45713/', '[1] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45644, 'Calcium Phosphate', 'Tolevamer', 'Moderate', 'INTERVAL: Simultaneous administration of cation-donating preparations may reduce the potassium exchange capability of cation-exchange resins due to binding of the cation to the resin.', NULL, 'Patients should consider separating the times of administration of the cation-exchange resin and any cation-donating preparation (e.', NULL, 'Patients should consider separating the times of administration of the cation-exchange resin and any cation-donating preparation (e.g., mineral supplements; antacids; products containing antacids such as didanosine buffered tablets or pediatric oral solution) by several hours if possible.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45714/', '[1] "Product Information. Kayexalate (sodium polystyrene sulfonate)." Sanofi Winthrop Pharmaceuticals, New York, NY.[2] "Product Information. Resonium Calcium (calcium polystyrene sulfonate)." Sanofi-Synthelabo Canada Inc, Markham, ON.[3] "Product Information. Kayexalate (sodium polystyrene sulfonate)." Sanofi Winthrop Pharmaceuticals, New York, NY.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363021);
