INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45717, 'Ibandronate', 'Flucytosine', 'Moderate', 'Drugs that impair glomerular filtration may prolong the half-life of flucytosine and lead to accumulation.', NULL, 'Caution is advised if flucytosine is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if flucytosine is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents).', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45787/', '[1] "Product Information. Ancobon (flucytosine)." Roche Laboratories, Nutley, NJ.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45718, 'Ibandronate', 'Flurbiprofen', 'Moderate', 'Theoretical concerns exist regarding the potential for increased risk and severity of gastrointestinal toxicity during coadministration of oral bisphosphonates and nonsteroidal anti-inflammatory drugs (NSAIDs) due to additive or synergistic irritant effects on the gastrointestinal mucosa. Because NSAIDs reduce the rate of ulcer healing in the stomach and duodenum, it is also possible that NSAIDs may delay healing and exaggerate the mucosal injury caused by oral bisphosphonates. Theoretical concerns exist regarding the potential for increased risk and severity of renal impairment during coadministration of bisphosphonates with high dosages or chronic use of NSAIDs due to additive or synergistic nephrotoxic effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure. Cases have primarily involved intravenous formulations of the drugs such as pamidronic acid and zoledronic acid, especially when they are administered too rapidly. The risk of hypocalcemia may also be increased, as drug-induced renal tubular damage can lead to renal loss of calcium and other electrolytes such as magnesium. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium. Chronic use of nonsteroidal anti-inflammatory drugs (NSAIDs) may be associated with renal toxicities including elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure.', NULL, 'Caution is advised if bisphosphonates are prescribed in combination with NSAIDs.', NULL, 'Caution is advised if bisphosphonates are prescribed in combination with NSAIDs. Patients receiving oral bisphosphonates should be closely monitored for the development of gastrointestinal toxicity and advised to immediately report potential signs and symptoms such as severe abdominal pain, nausea, vomiting, diarrhea, loss of appetite, dizziness, lightheadedness, and/or black, tarry stools. Patients receiving intravenous formulations of bisphosphonates should have renal function and serum electrolytes closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45788/', '[1] "Product Information. Actonel (risedronate)." Procter and Gamble Pharmaceuticals, Cincinnati, OH.[2] "Product Information. Bonefos (clodronate)." Rhone-Poulenc Rorer Canada Inc, Laval, QC.[3] "Product Information. Didronel I.V. Infusion (etidronate)." MGI Pharma Inc, Minnetonka, MN.[4] Graham, Malaty "Alendronate and naproxen are synergistic for development of gastric ulcers (Vol 161, pg 107, 1921)." Arch Intern Med 161 (2001): 1862[5] Graham DY, Malaty HM "Alendronate and naproxen are synergistic for development of gastric ulcers." Arch Intern Med 161 (2001): 107-10[6] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[7] "Product Information. Fosamax (alendronate)." Merck & Co, Inc, West Point, PA.[8] "Product Information. Aredia (pamidronate)." Novartis Pharmaceuticals, East Hanover, NJ.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] "Product Information. Didronel Tablets (etidronate)." Procter and Gamble Pharmaceuticals, Cincinnati, OH.[11] "Product Information. Boniva (ibandronate)." Roche Laboratories, Nutley, NJ.[12] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (45719, 'Ibandronate', 'Gentamicin', 'Moderate', 'Coadministration of bisphosphonates and aminoglycosides may potentiate the risks of hypocalcemia and nephrotoxicity, particularly when the bisphosphonate is administered intravenously. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium, while aminoglycosides can induce renal tubular damage resulting in renal loss of calcium and magnesium. Hypomagnesemia, in turn, may also lead to peripheral parathyroid hormone resistance and impaired parathyroid hormone secretion, thus compromising the normal homeostatic response to hypocalcemia.', NULL, 'Caution is advised if bisphosphonates are used in patients who have recently received or are receiving treatment with aminoglycosides.', NULL, 'Caution is advised if bisphosphonates are used in patients who have recently received or are receiving treatment with aminoglycosides. Renal function as well as serum calcium and magnesium levels should be monitored during and after treatment, since renal loss of calcium and magnesium can continue for many weeks after cessation of aminoglycosides, as can the effect of bisphosphonates. Patients and clinicians should be alert to signs and symptoms of clinical hypocalcemia such as muscle spasms, carpopedal spasm, laryngeal spasm, tetany, convulsions, mental changes (irritability, depression, psychosis), increased intracranial pressure, papilledema, QT interval prolongation, and cardiac arrhythmias.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/45789/', '[1] Chang JT, 