INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351320, 'Mirtazapine', 'Apalutamide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of mirtazapine, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The possibility of diminished therapeutic response to mirtazapine should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifampin, and St. John''s wort. Pharmacologic response to mirtazapine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'The possibility of diminished therapeutic response to mirtazapine should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
