INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351326, 'Naldemedine', 'Apalutamide', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G. Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.', 'DDInter', 1767317475, 'Concomitant use of naldemedine with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', 'Metabolism', 'Concomitant use of naldemedine with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
